const passwordValidation=function(){const e=document.querySelector("#username"),r=document.querySelector("#password"),o=document.querySelector("#repeatPassword");document.querySelector(".registration__submit").addEventListener("click",t=>{t.preventDefault(),""===e.value||""===r.value||""===o.value?popup.open("error",{error:"Empty fields"}):r.value!=o.value&&popup.open("error",{error:"Password mismatch"})})}(document);